  const transformedArray = [];
  const branchNames = inputData.branchNames;
  const leafNames = inputData.leafNames;

  /**
   * Funci贸n recursiva para recorrer la estructura de datos.
   * @param {object|array} node - El nodo actual en el recorrido.
   * @param {Array<string>} path - Un array que almacena las claves del camino actual.
   */
  function traverse(node, path) {
    // Caso base: si el nodo actual es un array, hemos llegado a los valores hoja.
    if (Array.isArray(node)) {
      const newObject = {};
      // Asigna los valores de las ramas usando los nombres del inputData
