        if (branchNames[index]) {
          newObject[branchNames[index]] = key;
        }
      });
      // Asigna los valores de las hojas
      node.forEach((value, index) => {
        if (leafNames[index]) {
          newObject[leafNames[index]] = value;
        }
      });
      transformedArray.push(newObject);
      return;
    }

    // Paso recursivo: si es un objeto, contin煤a el recorrido.
    for (const key in node) {
      if (Object.prototype.hasOwnProperty.call(node, key)) {
        traverse(node[key], [...path, key]);
      }
    }
  }

  // Comienza la recursi贸n desde el objeto de 'values'
